INSERT INTO `wr_page` VALUES (1,'中心介绍','','about','','','<b>[ 唯识深层沟通济南蔚然中心 ]</b> 是唯识深层沟通总部在济南东部区域开设的重点中心，本中心为对心灵成长有急切需求，对自我认知更加清晰，对人生完美有渴望的灵魂提供完整成熟的专业唯识深层技术服务，传播科学系统的身心灵生活方式，与内在的成长理念，致力于让唯识深层沟通技术帮助到更多的人，协助这些人唤醒内心深处的爱和智慧，找到心灵最深处的光，回归内在，与最完美的本我相遇。','<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;font-size:16px;background-color:#FFFFFF;\">\r\n	<strong><span style=\"color:#3DA742;\"><span style=\"font-family:宋体;\">唯识深层沟通济南蔚然中心：</span></span></strong><span style=\"font-family:宋体;\">是唯识深层沟通总部在济南东部区域开设的重点中心，本中心为对心灵成长有急切需求，对自我认知更加清晰，对人生完美有渴望的灵魂提供完整成熟的专业唯识深层技术服务，传播科学系统的身心灵生活方式，与内在的成长理念，致力于让唯识深层沟通技术帮助到更多的人，协助这些人唤醒内心深处的爱和智慧，找到心灵最深处的光，回归内在，与最完美的本我相遇。</span> \r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;font-size:16px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:宋体;\"><br />\r\n</span> \r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;font-size:16px;background-color:#FFFFFF;\">\r\n	<strong><span style=\"font-family:宋体;\"></span></strong> \r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;font-size:16px;background-color:#FFFFFF;\">\r\n	<strong><span style=\"color:#3DA742;\">中心地址：</span></strong>济南市历下区盛景花园小区10号楼2单元602室（奥体中路与轻风路交叉口西南），环境优美，清新安静，为济南及周边地区的学员及需要自我成长的朋友们提供了一个极其便利与亲近的心灵家园和爱的港湾。&nbsp;\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;font-size:16px;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;font-size:16px;background-color:#FFFFFF;\">\r\n	&nbsp;<span style=\"color:#3DA742;\"><strong>中心愿景：</strong></span>愿迷惑者得以清明；愿痛苦者得以安康；愿我们的生命得以完整。\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;font-size:16px;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;font-size:16px;background-color:#FFFFFF;\">\r\n	&nbsp;<strong><span style=\"color:#3DA742;\">中心使命：</span></strong>协助个人的生命觉醒；推动人类的意识转化；显化社会的幸福丰盛。&nbsp;\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;font-size:16px;background-color:#FFFFFF;\">\r\n	&nbsp;&nbsp;\r\n</p>\r\n<hr />\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;font-size:16px;background-color:#FFFFFF;\">\r\n	<span style=\"background-color:#00D100;\">&nbsp;心灵顾问微信号：</span> \r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;font-size:16px;background-color:#FFFFFF;\">\r\n	<strong><strong>心灵顾问No.1(<span style=\"background-color:#FFFFFF;\">weiran58969006</span>)、心灵顾问No.2(wr58969006)、心灵顾问No.3(1380631147)</strong></strong> \r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;font-size:16px;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;font-size:16px;background-color:#FFFFFF;\">\r\n	<span style=\"background-color:#00D100;\">中心负责人：</span> \r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;font-size:16px;background-color:#FFFFFF;\">\r\n	<strong>杨萧蔚： 138-0531-1479</strong> \r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;font-size:16px;background-color:#FFFFFF;\">\r\n	<br />\r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;font-size:16px;background-color:#FFFFFF;\">\r\n	<span style=\"font-family:汉仪大宋简;color:green;font-size:20px;\"><span><span style=\"font-family:宋体;\">PS：关注更多活动信息，敬请关注我们的微信平台。</span></span></span> \r\n</p>','','','','','static/images/s_logo.png','uploads/201309/logo.jpg',0,0,'',1322999570),(2,'联系我们','','contact','','','','<p style=\"font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;font-size:16px;background-color:#FFFFFF;\">\r\n	<span><b><span style=\"color:#E53333;background-color:#CCCCCC;\">联系方式：</span></b></span> \r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;font-size:16px;background-color:#FFFFFF;\">\r\n	<strong><strong><span style=\"color:#009900;\"><br />\r\n</span></strong></strong> \r\n</p>\r\n<p style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;font-size:16px;background-color:#FFFFFF;\">\r\n	<strong><strong><span style=\"color:#009900;\">心灵顾问微信号：心灵顾问No.1</span><span><span style=\"font-size:16px;\">(<span style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;font-size:16px;line-height:24px;background-color:#FFFFFF;\">weiran58969006</span>)、心灵顾问No.2(wr58969006)、心灵顾问No.3(1380631147)</span></span></strong></strong> \r\n</p>\r\n<p>\r\n	<strong><span style=\"color:#3DA742;\"><span style=\"color:#333333;\"><strong><span style=\"color:#3DA742;\"><span style=\"color:#009900;\"><span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\">中心电话</span>： &nbsp;</span></span></strong></span></span></strong><span style=\"color:#3DA742;\"><span style=\"color:#333333;\"><span style=\"color:#3DA742;\"><span style=\"color:#333333;font-size:16px;\">0531-58969006</span></span><span style=\"font-size:16px;\"></span></span></span> \r\n</p>\r\n<p>\r\n	<strong><span style=\"color:#009900;font-family:\'Microsoft YaHei\';font-size:16px;\">中心地址：&nbsp;</span></strong><span style=\"color:#3E3E3E;font-family:\'Helvetica Neue\', Helvetica, \'Hiragino Sans GB\', \'Microsoft YaHei\', Arial, sans-serif;line-height:25.6px;font-size:16px;background-color:#FFFFFF;\">济南市高新区康虹路新生活家园北区73号楼2单元302</span> \r\n</p>\r\n<p>\r\n	<strong><span style=\"color:#3DA742;\"><span style=\"color:#009900;font-family:\'Microsoft YaHei\';font-size:16px;\">中心网站</span><span style=\"font-family:\'Microsoft YaHei\';font-size:16px;\">：&nbsp;</span></span></strong><span><span style=\"line-height:25.6px;font-size:16px;background-color:#FFFFFF;\">www.jnwrzx.com</span></span> \r\n</p>\r\n<p>\r\n	<strong><span style=\"color:#009900;font-family:\'Microsoft YaHei\';font-size:16px;\">咨询邮箱</span></strong><strong><span style=\"color:#3DA742;font-family:\'Microsoft YaHei\';font-size:16px;\">：&nbsp;</span></strong><span style=\"color:#333333;font-size:16px;\"><span style=\"font-size:16px;line-height:25.6px;color:#333333;background-color:#FFFFFF;\">jnwrzx@126.com</span></span><span style=\"font-size:16px;\"></span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>','','','','','','',0,0,'',1322999588),(3,'蔚然文化','','cultural','','','企业文化是企业为解决生存和发展的问题的而树立形成的，被组织成员认为有效而共享，并共同遵循的基本信念和认知。 企业文化集中体现了一个企业经营管理的核心主张，以及由此产生的组织行为。','<p>\r\n	<span style=\"color:#333333;font-size:14px;line-height:36px;\">【愿景】</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#333333;\">&nbsp; &nbsp; ——愿迷惑者得以清明；愿痛苦者得以安康；愿安息者得以解脱；愿我们的生命得以完整。</span> \r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<strong><span style=\"font-size:18px;color:#9933E5;\"> </span></strong> \r\n</p>\r\n<p>\r\n	<span style=\"color:#333333;font-size:14px;line-height:36px;\">【使命】</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:14px;color:#333333;\">&nbsp; &nbsp; ——协助[个人]的生命觉醒，推动[人类]的意识转化，显化[社会]的幸福丰盛。</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:18px;color:#006600;\"><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"font-size:18px;color:#006600;\"><span style=\"font-size:18px;color:#9933E5;\"> </span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#333333;line-height:36px;font-size:14px;\">【独特技术】</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#333333;font-size:14px;\">&nbsp; &nbsp; ——[唯识深层沟通]技术</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#006600;\"><br />\r\n</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#006600;\"><span style=\"font-size:18px;color:#9933E5;\"> </span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#006600;\"><span style=\"color:#9933E5;\"></span></span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#333333;line-height:36px;font-size:14px;\">【核心理念】</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#333333;font-size:14px;\">&nbsp; &nbsp; ——[了解因果，发现自己，圆满人生]</span> \r\n</p>\r\n<p>\r\n	<span style=\"color:#333333;font-size:14px;\"><br />\r\n</span> \r\n</p>\r\n<br />\r\n<p>\r\n	<strong><span style=\"color:#9933E5;\">&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img src=\"/uploads/201603/5b0da90f1f08dda6f70f0aaa1960e5b3.png\" alt=\"\" /></span></strong> \r\n</p>\r\n<br />\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<br />\r\n</p>\r\n<p>\r\n	<strong><span style=\"font-size:18px;color:#9933E5;\"><br />\r\n</span></strong> \r\n</p>','','','','','','',0,0,'',1331877791),(4,'蔚然团队','','team','','','团队是现代企业管理中战斗的核心，几乎没有一家企业不谈团队，好象团队就是企业做大做强的灵丹妙药，只要抓紧团队建设就能有锦锈前程了。团队是个好东西，但怎样的团队才算一个好团队，怎样才能运作好一个团队呢？却是许多企业管理者不甚了然的，于是在企业团队建设的过程中就出现了许多弊病，例如从理论著作中生搬硬套到团队运作里面，是很难产生好团队的。','<div>\r\n	<div>\r\n		团队是现代企业管理中战斗的核心，几乎没有一家企业不谈团队，好象团队就是企业做大做强的灵丹妙药，只要抓紧团队建设就能有锦锈前程了。团队是个好东西，但怎样的团队才算一个好团队，怎样才能运作好一个团队呢？却是许多企业管理者不甚了然的，于是在企业团队建设的过程中就出现了许多弊病，例如从理论著作中生搬硬套到团队运作里面，是很难产生好团队的。任何理念都不能执着，执着生僵化，就会蜕变为形式主义，后果很糟糕。在如今企业管理者热火朝天进行的团队建设中就存在这个问题，将团队作为企业文化建设的至上准则是不恰当的，是不符合多元化的现实状况的。\r\n	</div>\r\n	<div>\r\n		一个优秀的企业管理者，应该怎样管理员工?道理也很简单，那就是要给员工创造一个充分利用自己的个性将工作干得最好的条件。不一定什么都要团队化，太死板了。虽然现在的企业也都提倡创新，但如果管理者过分强调团队精神，则员工的创新精神必然受到压抑。压抑个性就是压抑创新，没有个性哪来的创新?说得极端一点，企业管理者要谨防团队建设法西斯化。团队是需要的，企业管理者在团队建设的同时要遵循一个原则，不能压抑员工的个性。在团队内部，企业管理者要给员工充分的自由，少说几句少数服从多数，要知道，聪明的人在世界上还就占少数。\r\n	</div>\r\n	<div>\r\n		企业管理者应该解放思想，要有多元化的思维。不同的企业，团队的性质也不一样。要量体裁衣建设符合企业内在要求的团队，要灵活变化，别搞一刀切。如果该企业是劳动密集型企业，那你可以建设一支高度纪律性组织性的团队。如果该企业是知识密集型企业，那就要以自由主义来管理员工了，建立一支人尽其才的团队是最重要的，严格说算不上是团队，也没必要强调团队，更注重的应该是员工的个人创造力，千万别让团队束缚住员工的头脑，当然应该有的纪律和合作也是不可少的。如果企业既有创造型员工也有操作型员工，那可将团队建设重点放到操作型员工身上。需要注意的一点是，越聪明的人越倾向个人主义，这个情况，企业管理者要注意有的放矢。\r\n	</div>\r\n</div>\r\n<div>\r\n</div>','','','','','','',0,0,'Y',1379392484);
/*!40000 ALTER TABLE `wr_page` ENABLE KEYS */;
UNLOCK TABLES;

--
-- Table structure for table `wr_post`
--

DROP TABLE IF EXISTS `wr_post`;
/*!40101 SET @saved_cs_client     = @@character_set_client */;
/*!40101 SET character_set_client = utf8 */;
CREATE TABLE `wr_post` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `user_id` int(10) unsigned NOT NULL DEFAULT '1' COMMENT '用户',
  `nickname` varchar(30) NOT NULL DEFAULT '' COMMENT '用户名',
  `author` varchar(100) NOT NULL DEFAULT '' COMMENT '作者',
  `title` varchar(255) NOT NULL COMMENT '标题',
  `title_second` varchar(255) NOT NULL DEFAULT '' COMMENT '副标题',
  `title_alias` char(50) NOT NULL DEFAULT '' COMMENT '别名 ',
  `title_style` varchar(255) NOT NULL DEFAULT '' COMMENT '标题样式',
  `title_style_serialize` varchar(255) NOT NULL DEFAULT '' COMMENT '标题样式序列化',
  `html_path` varchar(100) NOT NULL DEFAULT '' COMMENT 'html路径',
  `html_file` varchar(100) NOT NULL DEFAULT '' COMMENT 'html文件名',
  `template` varchar(60) NOT NULL DEFAULT '' COMMENT '模板',
  `catalog_id` smallint(5) unsigned NOT NULL DEFAULT '0' COMMENT '分类',
  `special_id` smallint(5) unsigned NOT NULL DEFAULT '0' COMMENT '专题编号',
  `intro` text COMMENT '摘要',
  `image_list` text COMMENT '组图',
  `seo_title` varchar(255) NOT NULL DEFAULT '' COMMENT 'SEO标题',
  `seo_description` text COMMENT 'SEO描述',
  `seo_keywords` varchar(255) NOT NULL DEFAULT '' COMMENT 'SEO关键字',
  `content` mediumtext NOT NULL COMMENT '内容',
  `copy_from` varchar(100) NOT NULL DEFAULT '' COMMENT '来源',
  `copy_url` varchar(255) NOT NULL DEFAULT '' COMMENT '来源url',
  `redirect_url` varchar(255) NOT NULL DEFAULT '' COMMENT '跳转URL',
  `tags` varchar(255) NOT NULL DEFAULT '' COMMENT 'tags',
  `view_count` int(10) unsigned NOT NULL DEFAULT '1' COMMENT '查看次数',
  `commend` enum('Y','N') NOT NULL DEFAULT 'N' COMMENT '推荐',
  `attach_status` enum('Y','N') NOT NULL DEFAULT 'N' COMMENT '是否上传附件',
  `attach_file` varchar(255) NOT NULL DEFAULT '' COMMENT '附件名称',
  `attach_thumb` varchar(255) NOT NULL DEFAULT '' COMMENT '附件缩略图',
  `favorite_count` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '收藏数量',
  `attention_count` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '关注次数',
  `top_line` enum('Y','N') NOT NULL DEFAULT 'N' COMMENT '头条',
  `last_update_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '最后更新时间',
  `reply_count` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '回复次数',
  `reply_allow` enum('Y','N') NOT NULL DEFAULT 'Y' COMMENT '允许评论',
  `sort_desc` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '排序',
  `acl` varchar(100) NOT NULL DEFAULT 'Y' COMMENT '权限检测',
  `status_is` enum('Y','N') NOT NULL DEFAULT 'Y' COMMENT '新闻状态',
  `create_time` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '录入时间',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=52 DEFAULT CHARSET=utf8 COMMENT='内容管理';
/*!40101 SET character_set_client = @saved_cs_client */;

